"use strict";(self.webpackChunkwener_website=self.webpackChunkwener_website||[]).push([["807966"],{483269:function(e,n,t){t.r(n),t.d(n,{frontMatter:()=>r,toc:()=>d,default:()=>c,metadata:()=>o,assets:()=>l,contentTitle:()=>a});var o=JSON.parse('{"id":"os/busybox/udhcpd","title":"BusyBox udhcpd","description":"","source":"@site/../notes/os/busybox/udhcpd.md","sourceDirName":"os/busybox","slug":"/os/busybox/udhcpd","permalink":"/notes/os/busybox/udhcpd","draft":false,"unlisted":false,"editUrl":"https://github.com/wenerme/wener/edit/master/notes/../notes/os/busybox/udhcpd.md","tags":[{"inline":true,"label":"OS","permalink":"/notes/tags/os"},{"inline":true,"label":"BusyBox","permalink":"/notes/tags/busy-box"},{"inline":true,"label":"Network","permalink":"/notes/tags/network"},{"inline":true,"label":"DHCP","permalink":"/notes/tags/dhcp"}],"version":"current","lastUpdatedBy":"wener","lastUpdatedAt":1767451770000,"frontMatter":{"title":"BusyBox udhcpd","tags":["OS","BusyBox","Network","DHCP"]},"sidebar":"docs","previous":{"title":"udhcpc","permalink":"/notes/os/busybox/udhcpc"},"next":{"title":"CentOS","permalink":"/notes/os/centos/"}}'),s=t(486106),i=t(917776);let r={title:"BusyBox udhcpd",tags:["OS","BusyBox","Network","DHCP"]},a="udhcpd",l={},d=[];function p(e){let n={code:"code",h1:"h1",header:"header",pre:"pre",...(0,i.R)(),...e.components};return(0,s.jsxs)(s.Fragment,{children:[(0,s.jsx)(n.header,{children:(0,s.jsx)(n.h1,{id:"udhcpd",children:"udhcpd"})}),"\n",(0,s.jsx)(n.pre,{children:(0,s.jsx)(n.code,{className:"language-ini",children:"start		192.168.0.20\nend		192.168.0.254\n\ninterface	eth3\noption	dns	192.168.0.1\noption	subnet	255.255.255.0\n"})}),"\n",(0,s.jsx)(n.pre,{children:(0,s.jsx)(n.code,{className:"language-ini",children:'# Sample udhcpd configuration file (/etc/udhcpd.conf)\n# Values shown are defaults\n\n# The start and end of the IP lease block\nstart		192.168.0.20\nend		192.168.0.254\n\n# The interface that udhcpd will use\ninterface	eth0\n\n# The maximum number of leases (includes addresses reserved\n# by OFFER\'s, DECLINE\'s, and ARP conflicts). Will be corrected\n# if it\'s bigger than IP lease block, but it ok to make it\n# smaller than lease block.\n#max_leases	254\n\n# The amount of time that an IP will be reserved (leased to nobody)\n# if a DHCP decline message is received (seconds)\n#decline_time	3600\n\n# The amount of time that an IP will be reserved\n# if an ARP conflict occurs (seconds)\n#conflict_time	3600\n\n# How long an offered address is reserved (seconds)\n#offer_time	60\n\n# If client asks for lease below this value, it will be rounded up\n# to this value (seconds)\n#min_lease	60\n\n# The location of the pid file\n#pidfile	/var/run/udhcpd.pid\n\n# The location of the leases file\n#lease_file	/var/lib/misc/udhcpd.leases\n\n# The time period at which udhcpd will write out leases file.\n# If this is 0, udhcpd will never automatically write leases file.\n# Specified in seconds.\n#auto_time	7200\n\n# Every time udhcpd writes a leases file, the below script will be called\n#notify_file			# default: no script\n#notify_file	dumpleases	# useful for debugging\n\n# The following are BOOTP specific options\n# next server to use in bootstrap\n#siaddr		192.168.0.22	# default: 0.0.0.0 (none)\n# tftp server name\n#sname		zorak		# default: none\n# tftp file to download (e.g. kernel image)\n#boot_file	/var/nfs_root	# default: none\n\n# NOTE: "boot_file FILE" and "opt bootfile FILE" are conceptually the same,\n# but "boot_file" goes into BOOTP-defined fixed-size field in the packet,\n# whereas "opt bootfile" goes into DHCP option 0x43.\n# Same for "sname HOST" and "opt tftp HOST".\n\n# Static leases map\n#static_lease 00:60:08:11:CE:4E 192.168.0.54\n#static_lease 00:60:08:11:CE:3E 192.168.0.44 optional_hostname\n\n# The remainder of options are DHCP options and can be specified with the\n# keyword \'opt\' or \'option\'. If an option can take multiple items, such\n# as the dns option, they can be listed on the same line, or multiple\n# lines.\n# Examples:\nopt	dns	192.168.10.2 192.168.10.10\noption	subnet	255.255.255.0\nopt	router	192.168.10.2\nopt	wins	192.168.10.10\noption	dns	129.219.13.81	# appended to above DNS servers for a total of 3\noption	domain	local\noption	lease	864000		# default: 10 days\noption	msstaticroutes	10.0.0.0/8 10.127.0.1		# single static route\noption	staticroutes	10.0.0.0/8 10.127.0.1, 10.11.12.0/24 10.11.12.1\n# Arbitrary option in hex or string form:\noption	0x08	01020304	# option 8: "cookie server IP addr: 1.2.3.4"\noption	14	"dumpfile"\n\n# Currently supported options [hex option value] (for more info, see options.c):\n#opt lease      NUM             # [0x33]\n#opt subnet     IP              # [0x01]\n#opt broadcast  IP              # [0x1c]\n#opt router     IP_LIST         # [0x03]\n#opt ipttl      NUM             # [0x17]\n#opt mtu        NUM             # [0x1a]\n#opt hostname   STRING          # [0x0c] client\'s hostname\n#opt domain     STRING          # [0x0f] client\'s domain suffix\n#opt search     STRING_LIST     # [0x77] search domains\n#opt nisdomain  STRING          # [0x28]\n#opt timezone   NUM             # [0x02] (localtime - UTC_time) in seconds. signed\n#opt tftp       STRING          # [0x42] tftp server name\n#opt bootfile   STRING          # [0x43] tftp file to download (e.g. kernel image)\n#opt bootsize   NUM             # [0x0d] size of that file\n#opt rootpath   STRING          # [0x11] (NFS) path to mount as root fs\n#opt wpad       STRING          # [0xfc] Web Proxy Auto Discovery Protocol\n#opt serverid   IP              # [0x36] default: server\'s IP\n#opt message    STRING          # [0x38] error message (udhcpd sends it on success too)\n#opt vlanid     NUM             # [0x84] 802.1P VLAN ID\n#opt vlanpriority NUM           # [0x85] 802.1Q VLAN priority\n# RFC 5071: PXELINUX Options\n#opt 0xd0       F100747E        # [0xd0] magic\n#opt pxeconffile STRING         # [0xd1]\n#opt pxepathprefix STRING       # [0xd2]\n#opt reboottime NUM             # [0xd3] bootstrap timeout\n# Options specifying server(s)\n#opt dns        IP_LIST         # [0x06]\n#opt wins       IP_LIST         # [0x2c]\n#opt nissrv     IP_LIST         # [0x29]\n#opt ntpsrv     IP_LIST         # [0x2a]\n#opt lprsrv     IP_LIST         # [0x09]\n#opt swapsrv    IP              # [0x10]\n# Options specifying routes\n#opt routes     IP_PAIR_LIST    # [0x21]\n#opt staticroutes   STATIC_ROUTES # [0x79] RFC 3442 classless static route option\n#opt msstaticroutes STATIC_ROUTES # [0xf9] same, using MS option number\n# Obsolete options, no longer supported\n#opt logsrv     IP_LIST # [0x07] 704/UDP log server (not syslog!)\n#opt namesrv    IP_LIST # [0x05] IEN 116 name server, obsolete (August 1979!!!)\n#opt cookiesrv  IP_LIST # [0x08] RFC 865 "quote of the day" server, rarely (never?) used\n#opt timesrv    IP_LIST # [0x04] RFC 868 time server, rarely (never?) used\n# TODO: in development\n#opt userclass  STRING          # [0x4d] RFC 3004. set of LASCII strings. "I am a printer" etc\n#opt sipsrv     STRING LIST     # [0x78] RFC 3361. flag byte, then: 0: domain names, 1: IP addrs\n#opt ip6rd ....                 # [0xd4] IPv6 rapid deployment\n'})})]})}function c(e={}){let{wrapper:n}={...(0,i.R)(),...e.components};return n?(0,s.jsx)(n,{...e,children:(0,s.jsx)(p,{...e})}):p(e)}},917776:function(e,n,t){t.d(n,{R:()=>r,x:()=>a});var o=t(7378);let s={},i=o.createContext(s);function r(e){let n=o.useContext(i);return o.useMemo(function(){return"function"==typeof e?e(n):{...n,...e}},[n,e])}function a(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(s):e.components||s:r(e.components),o.createElement(i.Provider,{value:n},e.children)}}}]);